import React from 'react';
import axios from 'axios';

export default class FormPut extends React.Component {

    state = {
        id: undefined,
    }


    handleChange = event => {
        this.setState({
            id: event.target.value
        });
    };


    handleSubmit = event => {
        event.preventDefault();

        // const id = {
        //     id : this.state.id,
        // }

        axios.put(`https://8y5uop0v1a.execute-api.sa-east-1.amazonaws.com/dev/log/${this.state.id}`)
            .then(res => {
                console.log(res.data);
            })
    };


    render() {
        return ( <
            form onSubmit = { this.handleSubmit } >

            <
            CustomInput onChange = { this.handleChange }
            labelText = "Informe o id..."
            id = "id"
            name = "id"
            formControlProps = {
                {
                    fullWidth: true
                }
            }
            inputProps = {
                {
                    type: "text",
                    endAdornment: ( <
                        InputAdornment position = "end" >
                        <
                        Email className = { classes.inputIconsColor }
                        /> <
                        /InputAdornment>
                    )
                }
            }
            /> <
            CustomInput onChange = { this.handleChange }
            labelText = "Informe o Arquivo_name..."
            id = "arquivo_name"
            name = "arquivo_name"
            formControlProps = {
                {
                    fullWidth: true
                }
            }
            inputProps = {
                {
                    type: "text",
                    endAdornment: ( <
                        InputAdornment position = "end" >
                        <
                        Email className = { classes.inputIconsColor }
                        /> <
                        /InputAdornment>
                    )
                }
            }
            /> <
            CustomInput onChange = { this.handleChange }
            labelText = "Bucket_name"
            id = "bucket_name"
            name = "bucket_name"
            formControlProps = {
                {
                    fullWidth: true
                }
            }
            inputProps = {
                {
                    type: "text",
                    endAdornment: ( <
                        InputAdornment position = "end" >
                        <
                        Icon className = { classes.inputIconsColor } >
                        lock_outline <
                        /Icon> <
                        /InputAdornment>
                    ),
                    autoComplete: "off"
                }
            }
            /> <
            Button type = "submit"
            simple color = "primary"
            size = "lg" >
            Edit <
            /Button> 

            <
            /form>  
        );
    }

}
